</template>

<script>
  import Vue from 'vue'
  import Menu from '~/components/Menu';
  import {mapGetters, mapActions} from 'vuex';
  import VTooltip from 'v-tooltip';

  Vue.use(VTooltip)

  export default {
    components: {
      Menu,
    },
    data() {
      return {
        menuIsOpened: false,
        scrolledOverDark: false,
        collectionTooltip: {
          content: '',
          placement: 'right',
          trigger: 'manual',
          show: false,
        },
      };
    },

    methods: {
      toggleMenu() {
        this.menuIsOpened = !this.menuIsOpened;
      },
      ...mapActions({
        deleteProduct: 'collection/deleteProduct',
      }),

      showCollectionTooltip() {
        if (this.products.length > 0) {
          let itemWord = this.products.length == 1 ? 'item' : 'items'
          this.collectionTooltip.content = `you have ${this.products.length} ${itemWord} in your collection`
        } else {
          this.collectionTooltip.content = `Add items to your Collection`
        }
        this.collectionTooltip.show = true;
      },
      hideCollectionTooltip() {
        this.collectionTooltip.show = false;
      },
      scrollTogle() {
        let windowHeight = document.documentElement.clientHeight;
        if (document.querySelector('.blockquote')) {
          let quoteTop = document.querySelector('.blockquote').getBoundingClientRect().top;

          if (quoteTop < 40) {
            this.scrolledOverDark = true
          } else {
            this.scrolledOverDark = false
          }

          let quoteBgHeight = 0;
          if (document.documentElement.clientWidth > 1023) quoteBgHeight = 560;
          else quoteBgHeight = document.querySelector('.blockquote').clientHeight;
          if (quoteTop + quoteBgHeight < 40) {
            this.scrolledOverDark = false
          }
        }
      },
      initScrollToggle() {
        window.addEventListener('scroll', this.scrollTogle);
      },
      destroyScrollToggle() {
        window.removeEventListener('scroll', this.scrollTogle);
      }
    },

    computed: {
      ...mapGetters({
        products: 'collection/getProducts',
        newProduct: 'collection/getLastProduct',
      }),

      totalCost() {
        return this.products.reduce((acc, item) => {
          return acc + (+item.data.price);
        }, 0);
      },
    },

    mounted() {
      if (this.$route.params.product) {
        setTimeout(() => { this.initScrollToggle() }, 1000);
      }
    },

    watch: {
    newProduct() {
        //New product added routine
        this.collectionTooltip.content = 'You added item to your collection'
        this.collectionTooltip.show = true;
        document.querySelector('.tooltip-overlay').classList.add('is-visible');

        setTimeout(() => {
          this.collectionTooltip.show = false;
          document.querySelector('.tooltip-overlay').classList.remove('is-visible');
        }, 2000)
      },
      $route (to, from){
        this.destroyScrollToggle();
        if (this.$route.params.product) {
          setTimeout(() => { this.initScrollToggle() }, 3000);
        }
      }
    },
    middleware: 'auth',

  };

</script>

<style lang="scss">

  .header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    z-index: 99;
    padding: 30px 0;
    transition: all .3s ease-in-out;
    @media (max-width: 767px) {
      padding: 16px 0;
    }

    &.hidden {
      opacity: 0;
      pointer-events: none;
    }
    &.is-dark {
      .header__burger {

        .fa {
          background-color: #001545;
          &:before,
          &:after {
            background-color: #001545;
          }
        }

      }
    }

    &__inner {
      display: flex;
      position: absolute;
      left: 0;
    }

    &__logo {
      svg {
        display: block;
        width: 55px;
        height: 22px;
        fill: #F2CF93;
        transition: fill .2s ease-in-out;
      }
    }

    &__collection {
      position: absolute;
      right: 0;
      color: #fff;
      width: 22px;
      height: 22px;
      cursor: pointer;

      svg {
        width: 15px;
        height: 14px;
        fill: none;
        stroke: #fff;
        transition: fill .2s ease-in-out, stroke .2s ease-in-out;

        &.collection-fill {
          fill: #fff;
        }
      }

      &:hover {
        svg {
          stroke: #363EFB;

          &.collection-fill {
            fill: #363EFB;
          }
        }
      }
    }

    .header__collection-active {
      opacity: 0;
    }

    &__burger {
      margin-left: auto;
      width: 16px;
      height: 11px;
